import { observer } from "mobx-react"
import { Icon } from "react-icons-kit"
import { heart, pen, location, lightbulb } from "react-icons-kit/typicons"
import { capitalize } from "lodash"
import Avatar from "./avatar"

const darkBG = "#1b2733"

@observer
class Item extends Component {
  state = { hover: false }
  show() {
    const { icon, onClick, name } = this.props
    const { hover } = this.state

    return (
      <btn
        $hover={hover}
        onMouseEnter={() => {
          this.setState({ hover: true })
        }}
        onMouseLeave={() => {
          this.setState({ hover: false })
        }}
        onClick={onClick}
      >
        <Icon style={{ color: "white" }} size={24} icon={icon} />
        <name $showName={hover}>{name}</name>
      </btn>
    )
  }

  styles = {
    btn: {
      padding: "3px 15px",
      opacity: 0.6,
      transition: "all 150ms ease-in",
      animationDelay: "300ms",
      position: "relative",
    },
    name: {
      opacity: 0,
      position: "absolute",
      left: 0,
      top: -20,
      pointerEvents: "none",
      background: darkBG,
      color: "rgba(255, 255, 255, 0.9)",
      transition: "all 100ms ease-in",
      padding: `5px 8px`,
      borderRadius: 5,
      fontSize: 14,
      fontWeight: 600,
    },
    showName: {
      opacity: 1,
      transform: `translateY(-20px)`,
    },
    hover: {
      opacity: 1,
    },
  }
}

@observer
export default class Tooltip extends Component {
  state = {
    show: false,
    cfi: null,
    left: 0,
    top: 0,
    comment: "",
    selectData: null,
    showComment: false,
  }

  lastCallbacks = {}

  componentWillMount() {
    window.addEventListener("mousedown", e => {
      if (!this.state.show) return
      let inTooltip = false
      let target = e.target
      do {
        if ((target.tagName || "").toLowerCase() === "tooltip") {
          inTooltip = true
        }
      } while ((target = target.parentNode))

      if (!inTooltip) {
        this.hide()
      }
    })

    window.onTooltipHide = () => {
      if (!this.state.show) return
      this.hide()
    }

    window.onShowToolbar = (cfi, x, y, options = {}) => {
      console.log("showing toolbar", cfi)
      this.setState({
        cfi,
        left: x,
        top: y,
        show: true,
        showComment: false,
      })
      this.lastCallbacks = options
    }
  }

  hide = () => {
    this.setState({ show: false })
    setTimeout(() => {
      this.setState({ showComment: false, comment: "" })
    }, 150)
  }

  onKeyDown = e => {
    const { store } = this.props

    if (e.keyCode === 27) {
      this.hide()
    }

    if (e.keyCode === 13 && e.metaKey) {
      store.addComment(this.state.cfi.hashCode(), {
        text: this.state.comment,
        cfi: this.state.cfi,
      })
      if (this.lastCallbacks.onCreate) {
        this.lastCallbacks.onCreate()
      }
      this.hide()
    }
  }

  onClick = name => {
    const eventName = `on${capitalize(name)}`

    const cb = this.lastCallbacks[eventName]

    if (cb) {
      cb()
    }
    this.setState({ show: false })
  }

  show() {
    const { show, left, top, showComment } = this.state

    return (
      <tooltip
        $hide={!show}
        $show={show}
        style={{
          left,
          top,
          background: showComment ? `rgba(255, 255, 255, 0.95)` : darkBG,
          boxShadow: showComment && `1px 1px 5px rgba(0, 0, 0, 0.1)`,
        }}
      >
        {showComment && (
          <comment>
            <row>
              <Avatar name="NC" />
              <textarea
                onKeyDown={this.onKeyDown}
                autoFocus
                onChange={e => this.setState({ comment: e.target.value })}
                value={this.state.comment}
                placeholder="bravo, brilliant"
              />
            </row>
          </comment>
        )}
        {!showComment && (
          <items>
            <Item
              icon={heart}
              name="like"
              onClick={() => this.onClick("like")}
            />
            <Item icon={lightbulb} name="save" />
            <Item icon={location} name="beacon" />
            <Item
              icon={pen}
              name="comment"
              onClick={() => this.setState({ showComment: true })}
            />
          </items>
        )}
      </tooltip>
    )
  }

  styles = {
    tooltip: {
      zIndex: 1000,
      position: "fixed",
      margin: 20,
      boxShadow: "0 0 0 1px #000, 0 8px 16px rgba(27,39,51,0.16)",
      transition: "opacity 70ms ease-in, transform 90ms ease-in",
      borderRadius: 5,
      border: `1px solid rgba(0, 0, 0, 0.2)`,
      padding: 5,
    },
    hide: {
      pointerEvents: "none",
      transform: `translate(0px, 10px)`,
      opacity: 0,
    },
    comment: {
      padding: "5px 3px",
      borderRadius: 5,
    },
    show: {
      transform: `translate(0px, 0px)`,
      opacity: 1,
    },
    items: {
      flexFlow: "row",
    },
    textarea: {
      border: "0px solid black",
      marginTop: 10,
      color: "rgba(0, 0, 0, 0.8)",
      lineHeight: 1.4,
      width: 300,
      height: 150,
      fontSize: 14,
    },
  }
}
