import Logo from "./logo"
export default class Site extends Component {
  show() {
    const ratio = 1.28
    const width = 800
    const quoteRatio = 0.73

    return (
      <site>
        <inner>
          <top>
            <Logo />
            <nav>
              <item>Books</item>
              <item>Postcast</item>
              <item>Public Books</item>
              <item>Contact</item>
            </nav>
            <login>login</login>
          </top>
          <mainContent>
            <big>Read and Annotate Books with the People You Love</big>
            <smaller>
              Mark up books to give to friends or collaborate in real-time.
            </smaller>
            <imgHolder>
              <img
                $screenshot
                width={width}
                height={width / ratio}
                src={require("./app.png")}
              />
            </imgHolder>
            <quote>
              <img
                $quoteImg
                width={90}
                height={90 / quoteRatio}
                src={require("./edgar.png")}
              />
              <quoteRight>
                <quoteText>
                  “Marking a book is literally an experience of your differences
                  or agreements with the author. It is the highest respect you
                  can pay him.”
                </quoteText>
                <quoteAuthor>― Edgar Allen Poe</quoteAuthor>
              </quoteRight>
            </quote>
            <bottom>
              <left>
                <feature>Unlimited access our library of 70,000+ books</feature>
                <feature>
                  Syncs automatically between phone, desktop, and tablets
                </feature>
              </left>
              <btn>Join for $6 / month</btn>
            </bottom>
          </mainContent>
        </inner>
      </site>
    )
  }

  styles = {
    site: {
      background: `#0828CC`,
      height: "100%",
      color: "white",
      padding: 40,
      flex: 1,
    },
    inner: {
      flex: 1,
    },
    mainContent: {
      display: "block",
    },
    top: {
      flexFlow: "row",
      alignItems: "center",
      justifyContent: "space-between",
    },
    nav: {
      flexFlow: "row",
      flex: 1,
      justifyContent: "space-between",
      marginLeft: 100,
      marginRight: 100,
    },
    item: {
      textTransform: "uppercase",
      fontSize: 12,
      fontWeight: 600,
    },
    login: {
      textTransform: "uppercase",
      padding: "10px 20px",
      border: "1px solid rgba(255, 255, 255, 0.95)",
      borderRadius: 20,
      width: 80,
      justifyContent: "center",
      alignItems: "center",
      fontSize: 12,
      fontWeight: 600,
    },
    big: {
      color: "white",
      fontWeight: 600,
      fontSize: 40,
      textAlign: "center",
      marginTop: 40,
    },
    smaller: {
      color: "white",
      fontSize: 30,
      textAlign: "center",
      marginTop: 10,
    },
    imgHolder: {
      alignItems: "center",
    },
    screenshot: {
      display: "block",
    },
    quote: {
      marginTop: -30,
      alignItems: "center",
      flexFlow: "row",
      justifyContent: "center",
    },
    quoteRight: {
      justifyContent: "center",
    },
    quoteAuthor: {
      fontSize: 24,
      fontWeight: 600,
      alignSelf: "flex-end",
    },
    quoteText: {
      width: 550,
      fontSize: 24,
      padding: 20,
    },
    quoteImg: {
      marginRight: 20,
      boxShadow: "1px 1px 20px rgba(0, 0, 0, 0.1)",
      borderRadius: "50%",
    },
    bottom: {
      marginTop: 40,
      flexFlow: "row",
      alignItems: "center",
      justifyContent: "center",
    },
    feature: {
      fontSize: 22,
      textAlign: "center",
      fontWeight: 600,
      marginTop: 3,
      marginBottom: 3,
    },
    left: {
      marginRight: 25,
    },
    btn: {
      marginLeft: 25,
      textTransform: "uppercase",
      padding: "10px 40px",
      border: "1px solid rgba(255, 255, 255, 0.95)",
      borderRadius: 20,
      justifyContent: "center",
      alignItems: "center",
      fontSize: 24,
      fontWeight: 600,
    },
  }
}
