const isMobile = mobileCheck()

const localhost = window.location.hostname
export default class Store {
  focusing = false

  apiBase = isDev
    ? `http://${localhost}:3000`
    : `https://booksy-vwufpzdwpq.now.sh`
  bookWidth = 650 // Math.max(300, Math.min(650, window.innerWidth - 200))

  @observable
  book = null

  @observable
  comments = {}

  @observable
  title = null

  @observable
  bookID = null

  commentsRef = null
  @observable
  bookUrl = null

  @observable
  page = 0

  @observable
  isPlaying = false

  @observable
  playTexts = {}

  nextPage = () => {
    if (this.focusing) {
      this.nextPlay()
    } else {
      this.rendition.next()
    }
  }

  prevPage = () => {
    this.rendition.prev()
  }

  getIframeFromWindow = contentsWindow => {
    const href = contentsWindow.document
      .querySelector("base")
      .getAttribute("href")

    return Array.from(document.querySelectorAll("iframe")).filter(
      iframe =>
        (iframe.getAttribute("srcdoc") || "").indexOf(`base href="${href}"`) >
        -1,
    )[0]
  }

  renderBook = async el => {
    /*
    this.book = ePub(
      "https://s3.amazonaws.com/epubjs/books/moby-dick/OPS/package.opf",
    )
    */
    const rendition = this.book.renderTo(el, {
      // width: this.bookWidth,
      height: "100%",
      manager: "continuous",
      flow: "paginated",
      width: "100%",
    })

    rendition.display()

    rendition.hooks.content.register((contents, view) => {
      this.attachPlays(contents)
      this.attachComments(contents.cfiBase)
    })

    rendition.themes.default(bookTheme)
    rendition.on("mousedown", () => {
      onTooltipHide()
    })

    rendition.on("selected", (cfiRange, contents) => {
      const range = contents.range(cfiRange)

      const frameRect = this.getIframeFromWindow(
        contents.window,
      ).getBoundingClientRect()
      const { left, top } = range.getBoundingClientRect()

      onShowToolbar(cfiRange, left + frameRect.left, top + 10 + frameRect.top, {
        onLike: () => {
          rendition.annotations.underline(cfiRange)
          contents.window.getSelection().removeAllRanges()
        },
        onCreate: () => {
          rendition.annotations.underline(cfiRange)
          contents.window.getSelection().removeAllRanges()
          this.attachComments(contents.cfiBase)
        },
      })
    })

    this.rendition = rendition
  }

  attachPlays = contents => {
    const elements = Array.from(contents.document.querySelectorAll("p")).filter(
      el => {
        const trimmed = el.textContent.trim()
        return trimmed !== "undefined" && trimmed.length > 0
      },
    )
    const frame = this.getIframeFromWindow(contents)
    console.log("attaching plays", elements, frame)
    if (!frame) {
      return false
    }
    const parent = frame.parentNode
    const playEl = document.createElement("div")
    playEl.classList.add("playList")

    const currentPlays = parent.querySelector(".playList")
    if (currentPlays) {
      currentPlays.remove()
    }

    parent.appendChild(playEl)

    const texts = elements.map(el => el.textContent)
    this.playTexts[contents.cfiBase] = texts

    const positions = elements.map((el, index) => {
      const { left, top, width, height } = el.getClientRects()[0]
      return { left, top, width, height, text: texts[index] }
    })

    render(
      <plays cfi-base={contents.cfiBase}>
        <Plays
          store={this}
          cfiBase={contents.cfiBase}
          positions={positions}
          texts={texts}
          isPlaying={this.isPlaying}
          onPlay={index => {
            this.play(contents.cfiBase, index)
          }}
        />
      </plays>,
      playEl,
    )
  }

  attachComments = cfiBase => {
    const cfiStart = `epubcfi(` + cfiBase
    const pageHashes = Object.keys(this.comments).filter(hash => {
      return hash && this.comments[hash].cfi.indexOf(cfiStart) === 0
    })

    pageHashes.forEach(hash => {
      this.rendition.annotations.underline(this.comments[hash].cfi)
    })

    const highlights = Array.from(document.querySelectorAll("g[data-epubcfi]"))
      .map(g => {
        const cfi = g.getAttribute("data-epubcfi")

        // make sure its the right page
        if (cfi.indexOf(cfiStart) !== 0) {
          return null
        }

        const rect = g.querySelector("rect")
        const frame = rect.parentNode.parentNode.parentNode
        return {
          frame,
          cfi,
          x: +rect.getAttribute("x"),
          y: +rect.getAttribute("y"),
        }
      })
      .filter(i => i !== null)

    const newDiv = document.createElement("div")

    highlights.forEach(({ frame, cfi, x, y }) => {
      newDiv.classList.add("comments")
      frame.appendChild(newDiv)
      render(
        <comment
          style={{
            position: "absolute",
            right: 0,
            top: y,
          }}
        >
          <Comments cfi={cfi} store={this} />
        </comment>,
        newDiv,
      )
    })
  }

  currentAudio = null

  say = text =>
    new Promise(resolve => {
      // const text = this.paragraphs[index]
      if (this.currentAudio) {
        this.currentAudio.pause()
      }

      const audio = new Audio(
        `${this.apiBase}/speak?text=${text}&speakingRate=2.2`,
      )

      this.currentAudio = audio

      audio.type = "audio/mp3"

      audio.play()

      audio.addEventListener("ended", () => {
        this.currentAudio = null
        resolve()
      })
    })

  play = async (cfiBase, index) => {
    this.focusing = true
    const text = this.playTexts[cfiBase][index]
    if (this.isPlaying) {
      this.killAudio()
    }

    this.isPlaying = { cfiBase, index }
    await sleep(1)
    // document.querySelector("play.active").scrollIntoView({ behavior: "smooth" })
    // await this.say(text)

    await sleep(100)

    this.nextPlay()

    return true
  }

  killAudio = () => {
    if (this.currentAudio) {
      this.currentAudio.pause()
    }
  }

  stopPlaying = () => {
    this.killAudio()
    this.isPlaying = false
  }

  deleteComment = key => {
    if (!confirm("Are you sure you would like to delete this comment?")) {
      return
    }

    const ID = Object.keys(this.comments).filter(
      para => this.comments[para].selected.hashCode() === key,
    )[0]
    const comments = omit(toJS(this.comments), ID)
    this.comments = comments
  }

  nextPlay = async () => {
    const { index, cfiBase } = this.isPlaying
    // if more in page
    if (index < this.playTexts[cfiBase].length - 1) {
      console.log("play next")
      this.play(cfiBase, index + 1)
    } else {
      console.log("next page")
      const bases = Array.from(
        document.querySelectorAll("plays[cfi-base]"),
      ).map(el => el.getAttribute("cfi-base"))

      const baseIndex = bases.indexOf(cfiBase)

      if (baseIndex === bases.length - 1) {
        this.nextPage()
        await sleep(1500)
        const newBase = last(
          Array.from(document.querySelectorAll("plays[cfi-base]")).map(el =>
            el.getAttribute("cfi-base"),
          ),
        )
        this.play(newBase, 0, 0)
      } else {
        this.play(bases[baseIndex + 1], 0, 0)
      }
    }
  }

  setBook = async url => {
    const base = `${this.apiBase}/download?url=`
    const downloadUrl = base + url

    const book = await getBook(downloadUrl)
    // const chapters = await getChapters(book, 0)

    this.toc = await book.loaded.navigation

    const meta = await book.loaded.metadata

    // await book.generatePagination()

    this.title = meta.title
    this.book = book

    // await book.locations.generate(1000)
    // toc: await getTOC(book)
  }

  addComment = (key, val) => {
    console.log("adding comment", key, val)
    const comments = { ...toJS(this.comments), [key]: val }
    // this.comments = comments
    this.commentsRef.set(comments)
  }

  resetComments = () => {
    this.commentsRef.set({})
  }

  setComments = snapshot => {
    const val = snapshot.val()
    if (val) {
      this.comments = snapshot.val()
    }
  }

  getPage = scrollY => {
    return Math.max(0, findIndex(this.pageTops, top => top > scrollY))
  }

  constructor(bookName) {
    window._store = this
    /*
    document.addEventListener(
      "scroll",
      debounce(() => {
        this.page = this.getPage(window.scrollY)
      }, 100),
    )
    */

    observe(
      this,
      "bookID",
      () => {
        return
        if (this.commentsRef) {
          this.commentsRef.off("value", this.setComments)
        }

        this.commentsRef = firebase
          .database()
          .ref(`${this.currentTitle}/comments`)

        this.commentsRef.on("value", this.setComments)
      },
      true,
    )

    observe(this, "bookUrl", () => {
      // const isDev = (window.location + "").indexOf("localhost") > -1
      this.setBook(this.bookUrl)
    })

    const url = new URL(window.location.href)
    this.bookID = url.pathname.split(`/read/`)[1]
    return
    this.urlRef = firebase.database().ref(`urls/${this.bookID}`)
    this.urlRef.once("value").then(snapshot => {
      this.bookUrl = snapshot.val().bookUrl
    })
  }
}
