import React from "react"
import parser from "htmlparser2"
import { HotKeys } from "react-hotkeys"
import { observable } from "mobx"
import { observer } from "mobx-react"
import Store from "../store"
import Site from "./site"
import Chapter from "./chapter"
import TOC from "./TOC"
import { smoothScrollTo } from "../helpers"
import Top from "./top"
import Status from "./status"
import Toolbar from "./toolbar"

const map = {
  nextPage: "right",
  prevPage: "left",
}

@observer
export default class Root extends Component {
  componentWillMount() {
    this.store = new Store()
    window._store = this.store
  }

  handlers = {
    nextPage: () => {
      smoothScrollTo(0, window.scrollY + window.innerHeight, 225)
    },
    prevPage: () => {
      smoothScrollTo(0, window.scrollY - window.innerHeight, 225)
    },
  }

  show() {
    const { book } = this.store

    if ((window.location + "").indexOf("?book") === -1) {
      return <Site />
    }

    if (!book) return <loading>loading</loading>

    return (
      <book style={{ padding: 10 }}>
        <Toolbar store={this.store} />
        <HotKeys keyMap={map}>
          <mainArea>
            <Top store={this.store} />
            <content>
              <HotKeys handlers={this.handlers}>
                <TOC store={this.store} />
                {book.chapters.map(html => (
                  <Chapter store={this.store} html={html} />
                ))}
              </HotKeys>
            </content>
            {false && <Status />}
          </mainArea>
        </HotKeys>
      </book>
    )
  }

  styles = {
    content: {
      margin: 30,
      maxWidth: 800,
      alignSelf: "center",
    },
    book: {
      display: "block",
      background: "white",
    },

    loading: {
      fontSize: 32,
      opacity: 0.6,
      marginTop: 200,
    },
  }
}
