import { heart } from "react-icons-kit/icomoon/heart"
import EPubStore from "./ePubStore"
import { Icon } from "react-icons-kit"
import FocusBottom from "./focusBottom"
import Avatar from "./avatar"
import { observable } from "mobx"
import { observer } from "mobx-react"
import { flatten, without, random, range, includes } from "lodash"

const isMobile = window.innerWidth < 800

class ActiveStore {
  @observable
  sentenceIndex = 0

  @observable
  wordIndex = -1

  sentences = []
  words = []
  timeoutID = null

  @observable
  ePubStore = null

  @observable
  bookID = null

  constructor({ text, onFinish, onBefore, index, parentStore }) {
    this.index = index
    this.parentStore = parentStore
    this.onBefore = onBefore
    this.onFinish = onFinish
    this.sentences = text
      .replace(/([.?!])\s*(?=[A-Z])/g, "$1|")
      .split("|")
      .map(sentence => sentence.split(" "))

    this.words = flatten(
      this.sentences.map((words, sentenceIndex) =>
        words.map((word, wordIndex) => ({ word, wordIndex, sentenceIndex })),
      ),
    )

    if (parentStore.isPlaying) {
      this.setTimeout(this.onNextWord, 500)
    }
  }

  get liked() {
    return this.parentStore.isLiked(this.index)
  }

  onPreviousSentence = () => {
    if (this.sentenceIndex === 0) {
      this.onBefore()
      return
    }

    // we're at the beginning, let's go to last sentence
    if (this.wordIndex <= 0) {
      this.sentenceIndex -= 1
      this.wordIndex = -1

      const nextWord = this.sentences[this.sentenceIndex][0]
      this.setTimeout(this.onNextWord, nextWord.length * 50)
      return
    }

    this.wordIndex = -1
    const nextWord = this.sentences[this.sentenceIndex][0]
    this.setTimeout(this.onNextWord, nextWord.length * 30)
  }

  onNextSentence = () => {
    if (this.sentenceIndex < this.sentences.length - 1) {
      this.sentenceIndex += 1
      this.wordIndex = -1
      const nextWord = this.sentences[this.sentenceIndex][0]
      this.setTimeout(this.onNextWord, nextWord.length * 30)
      return
    }

    this.onFinish()
  }

  clearTimer = () => {
    if (this.timeoutID) {
      clearTimeout(this.timeoutID)
    }
  }

  say = text =>
    new Promise(resolve => {
      console.log("saying text is", text)
      // const text = this.paragraphs[index]
      const audio = new Audio(
        `${Settings.apiBase}/speak?text=${text}&pitch=-2&speakingRate=1.5`,
      )

      this.currentAudio = audio

      audio.type = "audio/mp3"

      audio.play()

      audio.addEventListener("ended", () => {
        this.currentAudio = null
        resolve()
      })
    })

  stopTimers = () => {
    if (this.timeoutID) {
      clearTimeout(this.timeoutID)
    }

    if (this.currentAudio) {
      this.currentAudio.pause()
    }
  }

  setTimeout = (fn, ms) => {
    if (this.timeoutID) {
      clearTimeout(this.timeoutID)
    }

    // only do things if we're playing
    if (this.parentStore.isPlaying) {
      this.timeoutID = setTimeout(fn, ms)
    }
  }

  onPlay = () => {
    if (this.currentAudio) {
      this.currentAudio.play()
    }
    this.setTimeout(this.onNextWord, 300)
  }

  onPause = () => {
    if (this.timeoutID) {
      clearTimeout(this.timeoutID)
    }
    if (this.currentAudio) {
      this.currentAudio.pause()
    }
  }

  audioFinished = false

  onNextWord = async () => {
    const toNextWord = () => {
      this.wordIndex += 1
      const nextWord = this.sentences[this.sentenceIndex][this.wordIndex]
      this.setTimeout(
        this.onNextWord,
        startOfSentence ? 700 : nextWord.length * 30,
      )
    }

    const startOfSentence = this.wordIndex === 0
    if (startOfSentence) {
      toNextWord()
      await this.say(this.sentences[this.sentenceIndex].join(" "))
      this.onNextSentence()
      return
    }

    if (this.wordIndex < this.sentences[this.sentenceIndex].length - 1) {
      toNextWord()
      return
    }
  }
}

class FocusStore {
  @observable
  texts = []

  @observable
  isPlaying = false

  @observable
  likes = []

  @observable
  activeIndex = -1

  @observable
  activeChapter = 6

  @observable
  active = null

  constructor() {
    const url = new URL(window.location.href)
    this.bookID = url.pathname.split(`/read/`)[1]
    this.start()
  }

  get activeChapterTitle() {
    if (this.texts.length === 0) return "loading"
    return Array.from(
      this.ePubStore.chapters[this.activeChapter].querySelectorAll("p"),
    )[0].textContent
  }

  start = async () => {
    await this.watchBookId()
    await this.setChapter(6)
    setTimeout(() => {
      this.setActive(0)
    }, 300)
  }

  setChapter = async index => {
    if (index < 0 || index > this.ePubStore.chapters.length - 1) {
      return false
    }
    this.activeChapter = index
    this.texts = await this.ePubStore.getChapter(index)
    return true
  }

  watchBookId = async () => {
    this.urlRef = firebase.database().ref(`urls/${this.bookID}`)
    const snapshot = await this.urlRef.once("value")
    const url = snapshot.val().bookUrl
    this.ePubStore = new EPubStore({ url })
    await this.ePubStore.start()
    return true
  }

  like = (index = this.activeIndex) => {
    this.likes = this.isLiked(index)
      ? without(this.likes, index)
      : [...this.likes, index]
  }

  isLiked = index => {
    return includes(this.likes, index)
  }

  onTogglePause = () => {
    this.isPlaying = !this.isPlaying
    if (this.isPlaying) {
      this.active.onPlay()
    } else {
      this.active.onPause()
    }
  }

  setActive = index => {
    console.log("setting active", index)
    this.activeIndex = index
    this.active = new ActiveStore({
      parentStore: this,
      index,
      text: this.texts[index],
      onFinish: this.onNext,
      onBefore: this.onPrevious,
    })
  }

  onPrevious = async () => {
    if (this.activeIndex === 0) {
      if (await this.setChapter(this.activeChapter - 1)) {
        this.setActive(this.texts.length - 1)
      }
    } else {
      this.setActive(this.activeIndex - 1)
    }
  }

  onNext = async () => {
    if (this.activeIndex === this.texts.length - 1) {
      if (await this.setChapter(this.activeChapter + 1)) {
        this.setActive(0)
      }
    } else {
      this.setActive(this.activeIndex + 1)
    }
  }
}

