class MiniMap extends Component {
  show() {
    const { store } = this.props

    return (
      <minimap>
        {store.texts.map((para, index) => {
          return (
            <para
              key={store.activeChapter + ":" + index}
              $liked={includes(store.likes, index)}
              $active={index === store.activeIndex}
              onMouseDown={() => store.setActive(index)}
            >
              {para
                .split(" ")
                .slice(0, 20)
                .map((word, index) => (
                  <word key={index} style={{ width: word.length * 2 }} />
                ))}
            </para>
          )
        })}
      </minimap>
    )
  }

  styles = {
    minimap: {
      height: "100%",
      width: 120,
      overflow: "scroll",
      borderLeft: `1px solid rgba(0, 0, 0, 0.1)`,
      borderRight: `1px solid rgba(0, 0, 0, 0.1)`,
      // borderRadius: 5,
      paddingTop: 5,
      paddingBottom: 5,
      display: "block",
    },
    para: {
      flexFlow: "row",
      flexWrap: "wrap",
      padding: `4px 6px`,
      lineHeight: 1,
      transition: "background 300ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      borderLeft: `1px solid rgba(0, 0, 0, 0)`,
    },
    active: {
      borderLeft: `1px solid rgba(0, 0, 0, 0.7)`,
    },
    liked: {
      background: "rgba(234, 70, 60, 0.1)",
      borderLeft: `1px solid rgba(255,109,94, 1)`,
    },
    word: {
      height: 2,
      marginLeft: 1,
      marginRight: 1,
      marginTop: 1,
      marginBottom: 3,
      background: `rgba(0, 0, 0, 0.2)`,
    },
  }
}

@observer
class Sentences extends Component {
  show() {
    const { store, index, text, parentStore } = this.props
    const isActive = store !== null
    const { sentenceIndex, wordIndex } = store || {}
    const fontSize = text.length < 60 ? 35 : 22 - text.length / 150
    const words = isActive
      ? store.words
      : text
          .split(" ")
          .map((word, wordIndex) => ({ word, wordIndex, sentenceIndex: 0 }))

    const activeLike = isActive && parentStore.isLiked(index)

    return (
      <sentences $activeLike={activeLike} $liked={parentStore.isLiked(index)}>
        <heart $activeHeart={activeLike}>
          <list>
            {["NC"].map(name => (
              <item $row>
                <heartAvatar>
                  <Avatar name="NC" />
                </heartAvatar>
                <Icon
                  size={32}
                  style={{ color: `rgba(255,109,94,1)` }}
                  icon={heart}
                />
              </item>
            ))}
          </list>
        </heart>
        {words.map(({ word, wordIndex, sentenceIndex }) => {
          const finished =
            (isActive && sentenceIndex < store.sentenceIndex) ||
            (isActive &&
              sentenceIndex === store.sentenceIndex &&
              wordIndex <= store.wordIndex)
          const wasFinished = isActive && sentenceIndex < store.sentenceIndex

          return (
            <word
              style={{ fontSize }}
              key={sentenceIndex + ":" + wordIndex}
              $activeSentence={
                isActive && sentenceIndex === store.sentenceIndex
              }
            >
              {word}
              <barContainer>
                <bar
                  style={{
                    width: finished ? "100%" : "0%",
                    opacity: wasFinished ? 0 : 1,
