    heartAvatar: {
      marginRight: 10,
    },
    activeHeart: {
      transform: `translateX(-130px) scale(1)`,
      opacity: 1,
    },
    activeLike: {
      boxShadow: `1px 1px 30px rgba(255,109,94,0.2)`,
    },
    liked: {
      border: "1px solid rgba(255,109,94,0.7)",
