    word: {
      paddingLeft: 3,
      paddingRight: 3,
      display: "inline-block",
      transition: "all 150ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      position: "relative",
      borderBottom: "1px solid rgba(0, 0, 0, 0)",
      color: `rgba(0, 0, 0, 0.6)`,
      fontWeight: 400,
    },
    barContainer: {
      position: "absolute",
      bottom: -1,
      background: "rgba(0, 0, 0, 0)",
      left: 0,
      top: 0,
      right: 0,
