      check = true
  })(navigator.userAgent || navigator.vendor || window.opera)
  return check
}

const isMobile = checkMobile() || (window.location + "").indexOf("?mobile") > -1
const leftPad = (str, char, len) => (char.repeat(100) + str).slice(-len)

@observer
export default class Focus extends Component {
  componentWillMount() {
    this.store = new FocusStore()

    document.addEventListener("keydown", e => {
      // l
      if (!e.metaKey && e.keyCode === 76) {
        this.store.toggleLike()
      }

      if (!e.metaKey && e.keyCode === 67) {
        this.store.onComment(this.store.activeSectionKey, prompt("comment?"))
      }
      // space
      if (e.keyCode === 32) {
        this.store.onTogglePause()
      }

      // left
      if (e.keyCode === 37) {
        this.store.active.onPreviousSentence()
      }

      // up
      if (e.keyCode === 38) {
        this.store.onPrevious()
      }

      // down
      if (e.keyCode === 40) {
        this.store.onNext()
      }

      // right
      if (e.keyCode === 39) {
        this.store.active.onNextSentence()
      }
    })
  }

  show() {
    const { activeSection, sections, activeChapter } = this.store

    if (!this.store.ePubStore || !this.store.ePubStore.loaded) {
      return <Loading />
    }

    return (
      <focus className="focus">
        <Top
          store={this.store}
          middle={this.store.activeChapterTitle}
          right={
            !isMobile && (
              <page>
                Chapter {activeChapter + 1} Section{" "}
                {leftPad(
                  "" + (activeSection + 1),
                  "0",
                  ("" + sections.length).length,
                )}{" "}
                / {sections.length}
              </page>
            )
          }
        />
        {!isMobile && (
          <minimap>
            <MiniMap
              width={120}
              section={activeSection}
              onSectionClick={this.store.setSection}
              chapter={activeChapter}
              store={this.store}
            />
          </minimap>
        )}
        <inner>
          <sections>
            {sections.map((text, index) => (
              <para
                key={this.store.activeChapter + ":" + index}
                $now={index === activeSection}
                $past={index < activeSection}
                $future={index > activeSection}
                $next={index === activeSection + 1}
                style={{
                  zIndex: index === activeSection ? 5000 : 1000 - index,
                }}
              >
                <Section
                  index={index}
                  parentStore={this.store}
                  text={this.store.sections[index]}
                  store={index === activeSection ? this.store.active : null}
                />
              </para>
            ))}
          </sections>
        </inner>
        <bottom>
          <FocusBottom
            store={this.store}
            onTogglePause={() => this.store.onTogglePause()}
            onLike={this.store.toggleLike}
            onComment={() =>
              this.store.onComment(this.store.activeSectionKey, prompt(""))
            }
            onForward={this.store.onForward}
            onBackward={this.store.onPrevious}
            onToggleComments={this.store.onToggleComments}
          />
        </bottom>
      </focus>
    )
  }

  styles = {
    focus: {
      overflow: "hidden",
      width: "100%",
      height: "100%",
    },
    bottom: {
      alignItems: "center",
      position: "absolute",
      left: 0,
      bottom: 20,
      right: 0,
      height: 60,
    },
    minimap: {
      overflow: "scroll",
      borderLeft: `1px solid rgba(0, 0, 0, 0.1)`,
      borderRight: `1px solid rgba(0, 0, 0, 0.1)`,
      paddingTop: 5,
      paddingBottom: 5,
      position: "absolute",
      right: 100,
      top: 40,
      bottom: 0,
    },
    span: {
      display: "inline",
    },
    inner: {
      zIndex: 10,
      position: "relative",
      color: "black",
      margin: `40px 40px 40px 40px`,
      height: "100%",
      fontSize: 22,
    },
    para: {
      maxWidth: window.innerWidth - 40,
      transition: "all 350ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      position: "absolute",
      opacity: 0,
      top: 20,
      left: 0,
      right: 0,
      fontSize: 20,
      lineHeight: 1.33,
      letterSpacing: ".009em",
      fontFamily: `"SF Pro Display","SF Pro Icons","Helvetica Neue","Helvetica","Arial",sans-serif`,
    },
    past: {
      opacity: 0,
      transform: `translateY(-15px)`,
      // transform: `scale(0.8)`,
    },
    next: {
      opacity: 0.04,
      transform: `translateY(155px)`,
      // transform: `scale(0.8)`,
    },
    future: {
      transform: `translateY(325px)`,
      // transform: `scale(0.6)`,
      opacity: 0,
    },
    now: {
      opacity: 1,
      transform: `scale(1)`,
    },
    mid: {
      flexFlow: "row",
      alignItems: "center",
    },
    card: {
      background: "rgba(255, 255, 255, 1)",
      fontWeight: 400,
      fontSize: 22,
      marginLeft: -100,
      marginRight: -100,
      lineHeight: 1.4,
      border: `1px solid rgba(0, 0, 0, 0.6)`,
      boxShadow: `1px 1px 30px rgba(0, 0, 0, 0.2)`,
      padding: `20px 40px`,
      marginTop: 20,
      borderRadius: 8,
      color: "black",
      marginBottom: 20,
    },
    comments: {
      margin: `10px auto`,
      width: `60%`,
    },
    heart: {
      marginLeft: 25,
    },
  }
}
