import ReactHtmlParser, {
  processNodes,
  convertNodeToElement,
  htmlparser2,
} from "react-html-parser"
import { observer } from "mobx-react"
import Comments from "./comments"
import { Icon } from "react-icons-kit"
import { ic_play_circle_outline } from "react-icons-kit/md/ic_play_circle_outline"

/*
  <p>hello <i>to the</i> world</p>
*/

class PlayButton extends Component {
  state = { hover: false }

  speak = () => {
    const { index } = this.props
    _store.play(index)
  }

  show() {
    return (
      <play
        $hover={this.state.hover}
        onMouseLeave={() => this.setState({ hover: false })}
        onMouseEnter={() => this.setState({ hover: true })}
        onClick={this.speak}
      >
        <Icon size={24} icon={ic_play_circle_outline} />
      </play>
    )
  }

  styles = {
    play: {
      position: "absolute",
      top: 15,
      left: -15,
      opacity: 0.4,
      transition: "opacity 80ms ease-in",
    },
    hover: {
      opacity: 1,
    },
  }
}

@observer
class Paragraph extends Component {
  componentWillMount() {
    const { node } = this.props
    const text = node.children.map(child => child.data).join("")
    const index = _store.paragraphs.length

    this.setState({ text, index })
    _store.addParagraph(text)
  }

  show() {
    const { text, index } = this.state

    if (_store.comments && text && _store.comments[text.hashCode()]) {
      const comment = _store.comments[text.hashCode()]

      const pieces = text.split(comment.selected)

      return (
        <p $playing={_store.isPlaying === index} className="para">
          {!_store.isPlaying && <PlayButton index={index} />}
          {pieces[0]}
          <highlight style={{ display: "inline" }}>
            {comment.selected}
          </highlight>
          {pieces[1]}
          <Comments store={_store} list={[comment]} />
        </p>
      )
    }

    if (!text || (text + "").trim().length === 0) {
      return null
    }

    return (
      <p className="para" $playing={_store.isPlaying === index}>
        {!_store.isPlaying && <PlayButton index={index} />}
        {text}
      </p>
    )
  }
  styles = {
    p: {
      position: "relative",
    },
    playing: {
      background: "rgba(0, 0, 0, 0.05)",
      fontWeight: 450,
    },
  }
}

@observer
export default class Chapter extends Component {
  state = { index: null }

  componentWillMount() {
    _store.addParagraph()
    this.setState({})
  }
  show() {
    const { html, store } = this.props
    store.comments

    const transform = node => {
      if (
        node.name === "a" &&
        node.attribs &&
        node.attribs.class === "pageno"
      ) {
        return (
          <pageContainer number={node.attribs.id}>{node.data}</pageContainer>
        )
      }

      if (node.name === "p") {
        return <Paragraph node={node} />
      }
    }

    const options = {
      decodeEntities: true,
      transform,
    }

    return <chapter>{ReactHtmlParser(html, options)}</chapter>
  }

  styles = {
    img: {
      display: "none",
    },
  }
}
