import Top from "./top"
import Books from "./books"
import { range, random } from "lodash"
import { Icon } from "react-icons-kit"
import { ic_star_border } from "react-icons-kit/md/ic_star_border"
import { ic_star } from "react-icons-kit/md/ic_star"

const highlighted = Books.slice(0, 3)
const rest = Books.slice(3)

class Stars extends Component {
  show() {
    const { rating } = this.props

    return (
      <stars>
        {range(5).map(i => (
          <Icon icon={i < rating ? ic_star : ic_star_border} />
        ))}
      </stars>
    )
  }

  styles = {
    stars: {
      flexFlow: "row",
      justifyContent: "space-between",
    },
  }
}

class Highlight extends Component {
  show() {
    const { name, author, id } = this.props
    return (
      <highlight>
        <cover />
        <info>
          <top>
            <name>{name}</name>
            <by>by {author}</by>
          </top>
          <bottom>
            <rating>
              <Stars rating={3} />
              <numRatings>{random(500, 2000)} ratings</numRatings>
            </rating>
            <read
              onClick={() => {
                window.location = `/read?bookUrl=http://www.gutenberg.org/ebooks/${id}.epub.noimages`
              }}
            >
              read
            </read>
          </bottom>
        </info>
      </highlight>
    )
  }

  styles = {
    highlight: {
      flex: 1,
      height: 120,
      border: `1px solid rgba(0, 0, 0, 0.1)`,
      padding: 10,
      borderRadius: 5,
      marginLeft: 20,
      marginRight: 20,
      background: `rgba(0, 0, 0, 0.08)`,
      boxShadow: `1px 1px 10px rgba(0, 0, 0, 0.1)`,
      flexFlow: "row",
      justifyContent: "space-between",
    },
    name: {
      textAlign: "center",
    },
    cover: {
      height: "100%",
      boxShadow: "1px 1px 5px rgba(0, 0, 0, 0.1)",
      width: 80,
      background: `rgba(0, 0, 0, 0.3)`,
      borderRadius: 4,
    },
    info: {
      paddingLeft: 10,
      paddingRight: 10,
      flex: 1,
      marginLeft: 15,
      justifyContent: "space-between",
    },
    rating: {
      marginTop: 15,
      justifyContent: "space-between",
      flexFlow: "row",
    },
    by: {
      fontSize: 14,
      textAlign: "right",
      marginTop: 5,
      fontWeight: 500,
    },
    read: {
      fontSize: 14,
      alignItems: "flex-end",
      fontWeight: 500,
    },
  }
}

class Book extends Component {
  show() {
    const { name, author, id } = this.props
    return (
      <book>
        <cover />
        <info>
          <name>{name}</name>
          <by>by {author}</by>
          <rating>
            <Stars rating={3} />
            <numRatings>{random(500, 2000)} ratings</numRatings>
          </rating>
          <read
            onClick={() => {
              window.location = `/read?bookUrl=http://www.gutenberg.org/ebooks/${id}.epub.noimages`
            }}
          >
            read
          </read>
        </info>
      </book>
    )
  }

  styles = {
    book: {
      marginTop: 10,
      marginBottom: 10,
      height: 120,
      border: `1px solid rgba(0, 0, 0, 0.1)`,
      padding: 10,
      borderRadius: 5,
      marginLeft: 20,
      marginRight: 20,
      background: `rgba(0, 0, 0, 0.08)`,
      boxShadow: `1px 1px 10px rgba(0, 0, 0, 0.1)`,
      flexFlow: "row",
      justifyContent: "space-between",
    },
    cover: {
      height: "100%",
      boxShadow: "1px 1px 5px rgba(0, 0, 0, 0.1)",
      width: 80,
      background: `rgba(0, 0, 0, 0.3)`,
      borderRadius: 10,
    },
    info: {
      paddingLeft: 10,
      paddingRight: 10,
    },
    by: {
      fontSize: 14,
      textAlign: "right",
      marginTop: 5,
      fontWeight: 500,
    },
    rating: {
      marginTop: 15,
      justifyContent: "space-between",
      flexFlow: "row",
    },
    by: {
      fontSize: 14,
      textAlign: "right",
      marginTop: 5,
      fontWeight: 500,
    },
  }
}

export default class Library extends Component {
  show() {
    const bookItem = ({ name, author, id }) => (
      <book>
        {name} by {author}
      </book>
    )

    return (
      <library>
        <inner>
          <Top />
          <libraryContent>
            <big>For You</big>
            <books>
              <highlighted>
                {highlighted.map(highlight => (
                  <Highlight {...highlight} />
                ))}
              </highlighted>
            </books>
            <big style={{ marginTop: 20 }}>Browse</big>
            <list>
              {rest.map(book => (
                <Book {...book} />
              ))}
            </list>
          </libraryContent>
        </inner>
      </library>
    )
  }

  styles = {
    library: {
      display: "block",
      flex: 1,
      background: `rgb(247, 236, 228)`,
      height: "100%",
    },
    inner: {
      width: 1100,
      margin: `0px auto`,
      paddingTop: 20,
    },
    libraryContent: {
      margin: 20,
    },
    big: {
      fontSize: 26,
      fontWeight: 600,
      width: 600,
    },
    highlighted: {
      flexFlow: "row",
      marginTop: 20,
    },
    book: {
      margin: 15,
    },
  }
}
