const width = 300
const height = 300

export default class Dirt extends Component {
  state = { frame: 0 }

  draw = canvas => {
    if (!canvas) return false
    const { frame } = this.state
    const w = width + 28,
      h = height
    const context = canvas.getContext("2d")
    const image = context.getImageData(0, 0, w, h)
    const indexes = []

    context.canvas.style.margin = "0 -14px"
    context.canvas.style.imageRendering = "pixelated"
    visit(w >> 1, h >> 1, 128, 128, 128)

    function visit(x, y, r, g, b) {
      if (x < 0 || x >= w || y < 0 || y >= h) return
      const i = y * w + x,
        j = i << 2
      if (image.data[j + 3] === 0) {
        image.data[j + 0] = r + (Math.random() < 0.5 ? -1 : 1)
        image.data[j + 1] = g + (Math.random() < 0.5 ? -1 : 1)
        image.data[j + 2] = b + (Math.random() < 0.5 ? -1 : 1)
        image.data[j + 3] = 255
        indexes.push(i)
      }
    }

    while (indexes.length) {
      const i = (Math.random() * indexes.length) | 0
      const j = indexes[i]
      const k = indexes.pop()
      if (i < indexes.length) indexes[i] = k

      const r = image.data[(j << 2) + 0]
      const g = image.data[(j << 2) + 1]
      const b = image.data[(j << 2) + 2]
      const x = j % w,
        y = (j / w) | 0
      visit(x - 1, y, r, g, b)
      visit(x + 1, y, r, g, b)
      visit(x, y - 1, r, g, b)
      visit(x, y + 1, r, g, b)

      if (!(frame % 1000)) {
        context.putImageData(image, 0, 0)
        setTimeout(() => {
          this.setState({ frame: frame + 1 })
        }, 30)
        return true
      }
    }

    context.putImageData(image, 0, 0)
    setTimeout(() => {
      this.setState({ frame: frame + 1 })
    }, 30)
  }

  show() {
    return <canvas width={width} height={height} ref={this.draw} />
  }

  styles = {
    canvas: {
      position: "fixed",
      bottom: 20,
      right: 20,
    },
  }
}
