import { heart } from "react-icons-kit/icomoon/heart"
import { observer } from "mobx-react"
import { Icon } from "react-icons-kit"
import Avatar from "./avatar"
import Comments from "./comments"

@observer
export default class Section extends Component {
  state = { showComments: false }
  componentWillReceiveProps({ store }) {
    const isActive = store !== null
    if (!this.props.store && isActive) {
      setTimeout(() => {
        this.setState({ showComments: true })
      })
    }

    if (!isActive) {
      setTimeout(() => {
        this.setState({ showComments: false })
      })
    }
  }

  show() {
    const { store, index, text, parentStore } = this.props
    const { showComments } = this.state
    const isActive = store !== null
    const fontSize = text.length < 60 ? 35 : 22 - text.length / 150
    const words = isActive
      ? store.words
      : text
          .split(" ")
          .map((word, wordIndex) => ({ word, wordIndex, sentenceIndex: 0 }))

    const key = isActive && parentStore.activeSectionKey
    const activeLike = isActive && parentStore.isLiked(key)
    const likes = isActive && store.likes

    let comments = []
    if (isActive) {
      comments = parentStore.getComments(key)
    }

    return (
      <sentences>
        <inner $activeLike={activeLike} $liked={parentStore.isLiked(index)}>
          <heart $activeHeart={activeLike}>
            <list>
              {(likes || []).map(({ user }) => (
                <item $row>
                  <heartAvatar>
                    <Avatar name={user} />
                  </heartAvatar>
                  <Icon
                    size={32}
                    style={{ color: `rgba(255,109,94,1)` }}
                    icon={heart}
                  />
                </item>
              ))}
            </list>
          </heart>
          {words.map(({ word, wordIndex, sentenceIndex }) => {
            const finished =
              (isActive && sentenceIndex < store.sentenceIndex) ||
              (isActive &&
                sentenceIndex === store.sentenceIndex &&
                wordIndex <= store.wordIndex)
            const wasFinished = isActive && sentenceIndex < store.sentenceIndex

            return (
              <word
                style={{ fontSize }}
                key={sentenceIndex + ":" + wordIndex}
                $activeSentence={
                  isActive && sentenceIndex === store.sentenceIndex
                }
              >
                {word}
                <barContainer>
                  <bar
                    style={{
                      width: finished ? "100%" : "0%",
                      opacity: wasFinished ? 0 : 1,
                    }}
                  />
                </barContainer>
              </word>
            )
          })}
        </inner>
        {comments.length > 0 && (
          <comments $showComments={showComments}>
            <Comments activeSection={store.activeComment} comments={comments} />
          </comments>
        )}
      </sentences>
    )
  }

  styles = {
    sentences: {
      // so it can be transitioned
    },
    inner: {
      display: "block",
      padding: `10px 20px`,
      position: "relative",
      border: "3px solid rgba(255,255,255,0)",
      boxShadow: `1px 1px 30px rgba(255,255,255,1)`,
      background: "white",
      background: "rgba(255, 255, 255, 1)",
      borderRadius: 5,
      transition: "all 450ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
    },
    item: {
      marginBottom: 20,
    },
    row: {
      flexFlow: "row",
    },
    comments: {
      transition: "all 900ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      transitionDelay: "200ms",
      opacity: 0,
      maxWidth: 450,
      margin: `40px auto`,
      transform: `translateY(40px) scale(0.8)`,
    },
    showComments: {
      opacity: 1,
      transform: `translateY(0px) scale(1)`,
    },
    heart: {
      position: "absolute",
      flexFlow: "row",
      left: 32,
      top: 10,
      opacity: 0,
      transition: "all 450ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      transform: `scale(0.5)`,
      zIndex: -1,
    },
    heartAvatar: {
      marginRight: 10,
    },
    activeHeart: {
      transform: `translateX(-130px) scale(1)`,
      opacity: 1,
    },
    activeLike: {
      boxShadow: `1px 1px 30px rgba(255,109,94,0.2)`,
      border: `3px solid rgba(255, 109, 94, 0.2)`,
    },
    liked: {
      border: `3px solid rgba(255, 109, 94, 0.4)`,
      background: `rgb(255, 252, 252)`,
      borderRadius: 5,
    },
    word: {
      paddingLeft: 3,
      paddingRight: 3,
      display: "inline-block",
      transition: "all 150ms cubic-bezier(0.785, 0.135, 0.150, 0.860)",
      position: "relative",
      borderBottom: "1px solid rgba(0, 0, 0, 0)",
      color: `rgba(0, 0, 0, 0.6)`,
      fontWeight: 400,
    },
    barContainer: {
      position: "absolute",
      bottom: -1,
      background: "rgba(0, 0, 0, 0)",
      left: 0,
      top: 0,
      right: 0,
    },
    bar: {
      width: "100%",
      height: "100%",
      opacity: 1,
      background: "rgba(0, 0, 0, 0.06)",
      transition: "width 250ms linear, opacity 1000ms ease-in",
    },
    active: {
      borderBottom: "1px solid rgba(0, 0, 0, 0.3)",
    },
    activeSentence: {
      color: `rgba(0, 0, 0, 1)`,
    },
  }
}
