import { heart, pen } from "react-icons-kit/typicons"
import { observer } from "mobx-react"
import { Icon } from "react-icons-kit"
import { backward } from "react-icons-kit/icomoon/backward"
import { pause } from "react-icons-kit/icomoon/pause"
import { play2 } from "react-icons-kit/icomoon/play2"
import { forward2 } from "react-icons-kit/icomoon/forward2"
import { commentsO } from "react-icons-kit/fa/commentsO"

const icons = {
  heart,
  pen,
  pause,
  comments: commentsO,
  play: play2,
  backward,
  forward: forward2,
}

@observer
class FocusIcon extends Component {
  state = { hover: false }
  show() {
    const { onClick, color = "rgba(0, 0, 0, 1)", name } = this.props
    const { hover } = this.state

    return (
      <btn
        $hover={hover}
        onMouseEnter={() => {
          this.setState({ hover: true })
        }}
        onMouseLeave={() => {
          this.setState({ hover: false })
        }}
        onClick={onClick}
      >
        <Icon style={{ color }} size={32} icon={icons[name]} />
      </btn>
    )
  }

  styles = {
    btn: {
      padding: "3px 15px",
      opacity: 0.6,
      transition: "all 150ms ease-in",
      animationDelay: "300ms",
      position: "relative",
    },
  }
}

@observer
export default class focusBottom extends Component {
  state = { hover: false }

  show() {
    const {
      onLike,
      onTogglePause,
      onComment,
      onBackward,
      onForward,
      onToggleComments,
      store,
    } = this.props
    const { hover } = this.state

    return (
      <bottom
        $hoverBottom={hover}
        onMouseEnter={() => this.setState({ hover: true })}
        onMouseLeave={() => this.setState({ hover: false })}
      >
        <inner>
          <left $row>
            <FocusIcon onClick={onBackward} name="backward" />
            <FocusIcon
              onClick={onTogglePause}
              name={store.isPlaying ? "pause" : "play"}
            />
            <FocusIcon onClick={onForward} name="forward" />
          </left>
          <right $row>
            <FocusIcon
              onClick={onLike}
              name="heart"
              tooltip={"Like"}
              color={`rgb(234,70,60)`}
            />
            <sayingComments $activeSayingComments={store.isSayingComments}>
              <FocusIcon
                onClick={onToggleComments}
                name="comments"
                tooltip={"Say Comments"}
                color={store.isSayingComments ? "#028402" : `rgb(0,0,0.3)`}
              />
            </sayingComments>
            <FocusIcon onClick={onComment} name="pen" tooltip={"Comment"} />
          </right>
        </inner>
      </bottom>
    )
  }

  styles = {
    bottom: {
      position: "fixed",
      zIndex: 5000,
      flexFlow: "row",
      alignItems: "center",
      height: 40,
      padding: `5px 30px`,
      alignItems: "center",
      justifyContent: "space-between",
      fontSize: 16,
      fontWeight: 600,
      opacity: 0.5,
      transition: "opacity 150ms ease-in",
    },
    sayingComments: {
      transition: "all 150ms ease-in",
      borderRadius: 3,
      border: "1px solid rgba(0, 0, 0, 0)",
    },
    activeSayingComments: {
      background: `rgba(217, 249, 255, 0.2)`,
      borderRadius: 20,
      boxShadow: `0px 0px 5px rgba(0, 0, 0, 0.05)`,
      border: `1px solid rgba(0, 0, 0, 0.2)`,
    },
    hoverBottom: {
      opacity: 1,
    },
    row: {
      flexFlow: "row",
      alignItems: "center",
    },
    logo: {
      fontFamily: `'Arbutus Slab', serif`,
      marginRight: 30,
      fontWeight: 100,
      color: "rgba(0, 0, 0, 0.8)",
      cursor: "pointer",
      fontSize: 25,
    },
    inner: {
      border: "1px solid rgba(0, 0, 0, 0.3)",
      background: `rgba(255, 255, 255, 0.9)`,
      boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
      margin: "0px auto",
      maxWidth: 500,
      alignItems: "center",
      padding: `15px 15px`,
      borderRadius: 5,
      justifyContent: "space-between",
      width: "100%",
      flexFlow: "row",
    },
    icon: {
      marginLeft: 20,
      marginRight: 20,
    },
  }
}
